        do {
            byte []outTok = aCtxt.accept(inTok, 0, inTok.length);

            if (outTok != null) {
                dos.writeInt(outTok.length);
                dos.write(outTok);
            }

            if (aCtxt.isEstablished())
                break;

            inTok = new byte[dis.readInt()];
            dis.readFully(inTok, 0, inTok.length);
        } while (true);

        exchangeWithPeer(aCtxt, dis, dos);
        aCtxt.dispose();
        client.close();
