        GSSCredential serverCreds = manager.createCredential(
            manager.createName(serviceName, null),
            GSSCredential.INDEFINITE_LIFETIME,
            mechOid,
            GSSCredential.ACCEPT_ONLY
        );

        GSSServer server = new GSSServer();
        server.s = new ServerSocket(port);
        server.print("Server listening on port " + port);

        while (true) {
            Socket client = server.s.accept();
            server.print("New client connection from " + client.getInetAddress());
            new Thread(() -> {
                try {
                    server.processClient(serverCreds, client);
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }).start();
        }
    }

    private void processClient(GSSCredential server, Socket client) throws Exception {
        DataInputStream dis = new DataInputStream(client.getInputStream());
        DataOutputStream dos = new DataOutputStream(client.getOutputStream());

        GSSManager manager = GSSManager.getInstance();
        GSSContext context = manager.createContext(server);

        byte[] token = new byte[dis.readInt()];
        dis.readFully(token);
        token = context.acceptSecContext(token, 0, token.length);

        if (token != null) {
            dos.writeInt(token.length);
            dos.write(token);
            dos.flush();
        }

        if (!context.isEstablished()) {
            token = new byte[dis.readInt()];
            dis.readFully(token);
            context.acceptSecContext(token, 0, token.length);
        }

        print("Security context established with " + context.getSrcName());
        exchangeWithPeer(context, dis, dos);

        context.dispose();
        client.close();
    }

    private void exchangeWithPeer(GSSContext aCtxt, DataInputStream dis, DataOutputStream dos) throws Exception {
        byte[] msg = new byte[dis.readInt()];
        dis.readFully(msg);
        msg = aCtxt.unwrap(msg, 0, msg.length, new MessageProp(false));

        String received = new String(msg);
        print("Received: " + received);

        String response = "Server response to: " + received;
        msg = response.getBytes();
        MessageProp prop = new MessageProp(false);
        msg = aCtxt.wrap(msg, 0, msg.length, prop);

        dos.writeInt(msg.length);
        dos.write(msg);
        dos.flush();
    }

    private void usage() {
        System.out.println("Usage: java GSSServer [-port port] [-mech mechOID] serviceName");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null) s.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
        System.exit(status);
    }
}

