        if (out == null) {
            throw new IllegalStateException("Not connected");
        }
        if (msgBytes.length > MAX_MSG_LEN) {
            throw new IllegalArgumentException("Message too long");
        }
        lenBuf[0] = (byte) (msgBytes.length >> 8);
        lenBuf[1] = (byte) msgBytes.length;
        out.write(lenBuf);
        out.write(msgBytes);
        out.flush();
    }

