        }
        msgLen = ((lenBuf[0] & 0xFF) << 8) | (lenBuf[1] & 0xFF);
        if (msgLen > MAX_MSG_LEN) {
            throw new IOException("Message too long");
        }
        read = in.read(msgBytes, 0, msgLen);
        if (read != msgLen) {
            throw new IOException("Failed to read full message");
        }
        byte[] result = new byte[msgLen];
        System.arraycopy(msgBytes, 0, result, 0, msgLen);
        return result;
    }

    public void close() throws IOException {
        if (in != null) {
            in.close();
            in = null;
        }
        if (out != null) {
