import java.net.CookieManager;
import java.net.CookiePolicy;
import java.net.HttpCookie;
import java.net.HttpURLConnection;
import java.net.URI;
import java.net.URISyntaxException;
import java.net.URL;
import java.util.List;
import java.util.Map;

public class HttpFetcher {
    private static final int MAX_REDIRECTS = 10;
    private static final CookieManager cookieManager = new CookieManager(null, CookiePolicy.ACCEPT_ALL);

    public static InputStream fetchUrl(String urlStr) {
        if (!urlStr.startsWith("http://") && !urlStr.startsWith("https://")) {
            return null;
        }

        HttpURLConnection connection = null;
        int redirectCount = 0;

        try {
            URL url = new URL(urlStr);
            while (redirectCount <= MAX_REDIRECTS) {
                connection = (HttpURLConnection) url.openConnection();
                connection.setInstanceFollowRedirects(false);

                String cookies = getCookiesForUrl(url);
                if (cookies != null) {
                    connection.setRequestProperty("Cookie", cookies);
                }

                connection.setRequestProperty("User-Agent", "Mozilla/5.0");
                connection.setRequestProperty("Accept", "text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8");
                connection.setRequestProperty("Accept-Language", "en-US,en;q=0.5");

                int responseCode = connection.getResponseCode();
                if (responseCode == HttpURLConnection.HTTP_OK) {
                    storeCookiesFromResponse(connection, url);
                    return connection.getInputStream();
                } else if (responseCode == HttpURLConnection.HTTP_MOVED_PERM ||
                           responseCode == HttpURLConnection.HTTP_MOVED_TEMP ||
                           responseCode == HttpURLConnection.HTTP_SEE_OTHER ||
                           responseCode == 307 || responseCode == 308) {
                    String location = connection.getHeaderField("Location");
                    if (location == null) {
                        return null;
                    }
                    storeCookiesFromResponse(connection, url);
                    url = new URL(url, location);
                    redirectCount++;
                } else {
                    return null;
                }
            }
        } catch (IOException | URISyntaxException e) {
            return null;
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
        return null;
    }

    private static String getCookiesForUrl(URL url) throws URISyntaxException {
        List<HttpCookie> cookies = cookieManager.getCookieStore().get(new URI(url.getProtocol(), url.getHost(), null, null));
        if (cookies.isEmpty()) {
            return null;
        }
        StringBuilder cookieStr = new StringBuilder();
        for (HttpCookie cookie : cookies) {
            if (cookieStr.length() > 0) {
                cookieStr.append("; ");
            }
            cookieStr.append(cookie.getName()).append("=").append(cookie.getValue());
        }
        return cookieStr.toString();
    }

    private static void storeCookiesFromResponse(HttpURLConnection connection, URL url) throws URISyntaxException {
        Map<String, List<String>> headerFields = connection.getHeaderFields();
        List<String> cookiesHeader = headerFields.get("Set-Cookie");
        if (cookiesHeader != null) {
            for (String cookie : cookiesHeader) {
                List<HttpCookie> httpCookies = HttpCookie.parse(cookie);
                for (HttpCookie httpCookie : httpCookies) {
                    cookieManager.getCookieStore().add(new URI(url.getProtocol(), url.getHost(), null, null), httpCookie);
                }
            }
        }
    }
}

