import java.io.*;
import java.net.*;
import java.nio.ByteBuffer;
import java.nio.charset.StandardCharsets;
import java.security.MessageDigest;
import java.security.NoSuchAlgorithmException;
import java.util.Base64;
import java.util.HashMap;
import java.util.Map;

public class WebSocketServer {
    private ServerSocket serverSocket;
    private final int port;

    public WebSocketServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        System.out.println("WebSocket server started on port " + port);
        handleConnection();
    }

    public void handleConnection() throws IOException {
        while (true) {
            try (Socket clientSocket = serverSocket.accept();
                 BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                 OutputStream out = clientSocket.getOutputStream()) {

                String line;
                Map<String, String> headers = new HashMap<>();
                while ((line = in.readLine()) != null && !line.isEmpty()) {
                    String[] parts = line.split(": ", 2);
                    if (parts.length == 2) {
                        headers.put(parts[0], parts[1]);
                    }
                }

                String key = headers.get("Sec-WebSocket-Key");
                if (key != null) {
                    String response = generateHandshakeResponse(key);
                    out.write(response.getBytes(StandardCharsets.UTF_8));
                    out.flush();

                    byte[] data = "Hello, WebSocket!".getBytes(StandardCharsets.UTF_8);
                    sendBinaryFrame(out, data, false);
                }
            }
        }
    }

    private String generateHandshakeResponse(String key) {
        String guid = "258EAFA5-E914-47DA-95CA-C5AB0DC85B11";
        String combined = key + guid;
        try {
            MessageDigest sha1 = MessageDigest.getInstance("SHA-1");
            byte[] hash = sha1.digest(combined.getBytes(StandardCharsets.UTF_8));
            String accept = Base64.getEncoder().encodeToString(hash);
            return "HTTP/1.1 101 Switching Protocols\r\n" +
                   "Upgrade: websocket\r\n" +
                   "Connection: Upgrade\r\n" +
                   "Sec-WebSocket-Accept: " + accept + "\r\n\r\n";
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException("SHA-1 not available", e);
        }
    }

    private void sendBinaryFrame(OutputStream out, byte[] data, boolean mask) throws IOException {
        ByteBuffer buffer = ByteBuffer.allocate(2 + (mask ? 4 : 0) + data.length);
        buffer.put((byte) (0x80 | 0x02));
        if (data.length <= 125) {
            buffer.put((byte) (mask ? 0x80 | data.length : data.length));
        } else if (data.length <= 65535) {
            buffer.put((byte) (mask ? 0x80 | 126 : 126));
            buffer.putShort((short) data.length);
        } else {
            buffer.put((byte) (mask ? 0x80 | 127 : 127));
            buffer.putLong(data.length);
        }

        if (mask) {
            byte[] maskingKey = new byte[4];
            new SecureRandom().nextBytes(maskingKey);
            buffer.put(maskingKey);
            for (int i = 0; i < data.length; i++) {
                data[i] ^= maskingKey[i % 4];
            }
        }

        buffer.put(data);
        out.write(buffer.array());
        out.flush();
    }

    public void stop() throws IOException {
        if (serverSocket != null && !serverSocket.isClosed()) {
            serverSocket.close();
        }
    }
}

