public class HttpUrlSource {
    private SourceInfoStorage sourceInfoStorage;
    private HeaderInjector headerInjector;
    private SourceInfo sourceInfo;
    private HttpURLConnection connection;
    private InputStream inputStream;

    public HttpUrlSource(SourceInfoStorage sourceInfoStorage, HeaderInjector headerInjector) {
        this.sourceInfoStorage = sourceInfoStorage;
        this.headerInjector = headerInjector;
    }

    public long length() throws IOException {
        if (sourceInfo == null || sourceInfo.getLength() == -1) {
            fetchContentInfo();
        }
        return sourceInfo.getLength();
    }

    public void open(long offset) throws IOException {
        close();
        connection = openConnection(offset, 8000);
        inputStream = connection.getInputStream();
    }

    public void close() throws IOException {
        if (inputStream != null) {
            inputStream.close();
            inputStream = null;
        }
        if (connection != null) {
            connection.disconnect();
            connection = null;
        }
    }

    public int read(byte[] buffer) throws IOException {
        if (inputStream == null) {
            throw new IllegalStateException("Connection not opened");
        }
        return inputStream.read(buffer);
    }

    public String getMime() throws IOException {
        if (sourceInfo == null || sourceInfo.getMime() == null) {
            fetchContentInfo();
        }
        return sourceInfo.getMime();
    }

    public String getUrl() {
        return sourceInfo != null ? sourceInfo.getUrl() : null;
    }

    public void fetchContentInfo() throws IOException {
        HttpURLConnection infoConnection = openConnection(0, 8000);
        try {
            sourceInfo = new SourceInfo(
                infoConnection.getURL().toString(),
                getContentLength(infoConnection),
                infoConnection.getContentType()
            );
            sourceInfoStorage.put(sourceInfo.getUrl(), sourceInfo);
        } finally {
            infoConnection.disconnect();
        }
    }

    private HttpURLConnection openConnection(long offset, int timeout) throws IOException {
        URL url = new URL(sourceInfo != null ? sourceInfo.getUrl() : sourceInfoStorage.getLastUrl());
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setConnectTimeout(timeout);
        connection.setReadTimeout(timeout);

        if (offset > 0) {
            connection.setRequestProperty("Range", "bytes=" + offset + "-");
        }

        injectCustomHeaders(connection, url.toString());

        int responseCode = connection.getResponseCode();
        if (responseCode == HttpURLConnection.HTTP_MOVED_PERM || responseCode == HttpURLConnection.HTTP_MOVED_TEMP) {
            String newUrl = connection.getHeaderField("Location");
            connection.disconnect();
            sourceInfoStorage.put(url.toString(), new SourceInfo(newUrl, -1, null));
            return openConnection(offset, timeout);
        }

        long availableBytes = readSourceAvailableBytes(connection, offset, responseCode);
        if (sourceInfo == null) {
            sourceInfo = new SourceInfo(
                connection.getURL().toString(),
                availableBytes,
                connection.getContentType()
            );
            sourceInfoStorage.put(sourceInfo.getUrl(), sourceInfo);
        }

        return connection;
    }

    private void injectCustomHeaders(HttpURLConnection connection, String url) {
        if (headerInjector != null) {
            Map<String, String> headers = headerInjector.getHeaders(url);
            for (Map.Entry<String, String> entry : headers.entrySet()) {
                connection.setRequestProperty(entry.getKey(), entry.getValue());
            }
        }
    }

    private long readSourceAvailableBytes(HttpURLConnection connection, long offset, int responseCode) {
        if (responseCode == HttpURLConnection.HTTP_PARTIAL) {
            String contentRange = connection.getHeaderField("Content-Range");
            if (contentRange != null) {
                int slashPos = contentRange.indexOf('/');
                if (slashPos != -1) {
                    return Long.parseLong(contentRange.substring(slashPos + 1));
                }
            }
        }
        return getContentLength(connection);
    }

    private long getContentLength(HttpURLConnection connection) {
        String contentLengthHeader = connection.getHeaderField("Content-Length");
        if (contentLengthHeader != null) {
            try {
                return Long.parseLong(contentLengthHeader);
            } catch (NumberFormatException e) {
                return -1;
            }
        }
        return -1;
    }

    public static class SourceInfo {
        private final String url;
        private final long length;
        private final String mime;

        public SourceInfo(String url, long length, String mime) {
            this.url = url;
            this.length = length;
            this.mime = mime;
        }

        public String getUrl() {
            return url;
        }

        public long getLength() {
            return length;
        }

        public String getMime() {
            return mime;
        }
    }

    public interface SourceInfoStorage {
        void put(String url, SourceInfo sourceInfo);
        SourceInfo get(String url);
        String getLastUrl();
    }

    public interface HeaderInjector {
        Map<String, String> getHeaders(String url);
    }
}

