                    }

                    streamToClient.close();

                } catch (IOException ex) {
                    log.warning("[CMPS Proxy] - General IO Exception caught in main Thread: " + ex.getMessage());
                    break;
                } finally {
                    try {
                        if (server != null) {
                            server.close();
