public class ConnectionManipulationProxyServer implements Runnable {
    private int localPort;
    private String host;
    private int remotePort;
    private Thread proxyThread;
    private final Object enableLock = new Object();
    private boolean enableProxy;
    private boolean running;
    private Socket client;
    private Socket server;
    private ServerSocket serverSocket;

    public ConnectionManipulationProxyServer(String host, int remotePort, int localPort) {
        this.host = host;
        this.remotePort = remotePort;
        this.localPort = localPort;
    }

    public void startProxy() {
        synchronized (enableLock) {
            if (running) {
                return;
            }
            running = true;
            enableProxy = true;
            proxyThread = new Thread(this);
            proxyThread.start();
        }
    }

    public void stopProxy() {
        synchronized (enableLock) {
            if (!running) {
                return;
            }
            running = false;
            enableProxy = false;
            killOpenSockets();
            if (proxyThread != null) {
                proxyThread.interrupt();
            }
        }
    }

    public void killOpenSockets() {
        try {
            if (client != null && !client.isClosed()) {
                client.close();
            }
            if (server != null && !server.isClosed()) {
                server.close();
            }
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
        } catch (IOException e) {
        }
    }

    public void run() {
        try {
            serverSocket = new ServerSocket(localPort);
            while (running) {
                try {
                    client = serverSocket.accept();
                    if (enableProxy) {
                        server = new Socket(host, remotePort);
                        new Thread(new ProxyForwarder(client.getInputStream(), server.getOutputStream())).start();
                        new Thread(new ProxyForwarder(server.getInputStream(), client.getOutputStream())).start();
                    } else {
                        client.close();
                    }
                } catch (IOException e) {
                }
            }
        } catch (IOException e) {
        } finally {
            killOpenSockets();
        }
    }

    public int getLocalPort() {
        return localPort;
    }

    private class ProxyForwarder implements Runnable {
        private InputStream input;
        private OutputStream output;

        public ProxyForwarder(InputStream input, OutputStream output) {
            this.input = input;
            this.output = output;
        }

        public void run() {
            byte[] buffer = new byte[4096];
            int bytesRead;
            try {
                while ((bytesRead = input.read(buffer)) != -1) {
                    output.write(buffer, 0, bytesRead);
                    output.flush();
                }
            } catch (IOException e) {
            } finally {
                try {
                    input.close();
                    output.close();
                } catch (IOException e) {
                }
            }
        }
    }
}

