        if (hostname != null) {
            myServerSocket = new ServerSocket(myPort, 50, InetAddress.getByName(hostname));
        } else {
            myServerSocket = new ServerSocket(myPort);
        }

        myThread = new Thread(() -> {
            while (!myServerSocket.isClosed()) {
                try {
                    Socket clientSocket = myServerSocket.accept();
                    new Thread(() -> {
                        try {
                            InputStream input = clientSocket.getInputStream();
                            OutputStream output = clientSocket.getOutputStream();
                            handleConnection(input, output);
                            clientSocket.close();
                        } catch (IOException e) {
                            e.printStackTrace();
                        }
                    }).start();
                } catch (IOException e) {
                    if (!myServerSocket.isClosed()) {
                        e.printStackTrace();
                    }
                }
            }
        });
        myThread.setDaemon(true);
        myThread.start();
    }

    public void stop() throws IOException, InterruptedException {
        if (myServerSocket != null) {
            myServerSocket.close();
        }
        if (myThread != null) {
            myThread.join();
        }
    }

    public abstract void handleConnection(InputStream inputStream, OutputStream outputStream) throws IOException;
