public class SnapshotService implements Runnable {
    private int port;
    private ServerSocket serverSocket;
    private boolean closed;

    public SnapshotService(int port) {
        if (port < 0 || port > 65535) {
            throw new IllegalArgumentException("Port must be between 0 and 65535");
        }
        this.port = port;
    }

    public void listen() {
        if (serverSocket != null) {
            throw new IllegalStateException("Server is already running");
        }
        openSocket(port);
    }

    public int openSocket(int port) {
        try {
            serverSocket = new ServerSocket(port);
            new Thread(this).start();
            return serverSocket.getLocalPort();
        } catch (IOException e) {
            throw new RuntimeException("Failed to open server socket", e);
        }
    }

    @Override
    public void run() {
        while (!closed) {
            try {
                Socket socket = serverSocket.accept();
                new Thread(() -> process(socket)).start();
            } catch (IOException e) {
                if (!isIgnoredException()) {
                    throw new RuntimeException("Error accepting client connection", e);
                }
            }
        }
    }

    private boolean isIgnoredException() {
        return closed || serverSocket == null || serverSocket.isClosed();
    }

    private void process(Socket socket) {
        try (BufferedReader inputReader = new BufferedReader(new InputStreamReader(socket.getInputStream()));
             BufferedWriter outputWriter = new BufferedWriter(new OutputStreamWriter(socket.getOutputStream()))) {
            String inputLine;
            while ((inputLine = inputReader.readLine()) != null) {
                String response = processCommand(inputLine);
                outputMessage(outputWriter, response);
