        } catch (IOException e) {
            if (!isIgnoredException()) {
                throw new RuntimeException("Error processing client connection", e);
            }
        } finally {
            try {
                socket.close();
            } catch (IOException e) {
                if (!isIgnoredException()) {
                    throw new RuntimeException("Error closing client socket", e);
                }
            }
        }
    }

    private String processCommand(String command) {
        return "Processed: " + command;
    }

    private void outputMessage(BufferedWriter outputWriter, String msg) throws IOException {
        outputWriter.write(msg);
        outputWriter.newLine();
        outputWriter.flush();
    }

    public void close() {
        closed = true;
        if (serverSocket != null) {
            try {
                serverSocket.close();
            } catch (IOException e) {
                throw new RuntimeException("Error closing server socket", e);
            }
        }
    }
}

