        String address = System.getProperty("binary.link.server.address");
        try {
            return address != null ? InetAddress.getByName(address) : InetAddress.getLoopbackAddress();
        } catch (UnknownHostException e) {
            return InetAddress.getLoopbackAddress();
        }
    }

    public int getListenPort() {
        String port = System.getProperty("binary.link.server.port");
        return port != null ? Integer.parseInt(port) : 0;
    }

    private class Acceptor extends Thread {
        private ServerSocket server;
        private InetAddress listenAddress;

        public Acceptor() throws IOException {
            listenAddress = getListenAddress();
            server = new ServerSocket(getListenPort(), 50, listenAddress);
        }

        public void run() {
            try {
                while (!server.isClosed()) {
                    Socket socket = server.accept();
                    Handler newHandler = new Handler(socket);
                    Handler oldHandler = handler.getAndSet(newHandler);
                    if (oldHandler != null) {
