public class MockPop3Server {
    private final Deque<Pop3Interaction> interactions;
    private final CountDownLatch waitForConnectionClosed;
    private final CountDownLatch waitForAllExpectedCommands;
    private MockServerThread mockServerThread;
    private final String host;
    private final int port;

    public MockPop3Server(String host, int port, Deque<Pop3Interaction> interactions) {
        this.host = host;
        this.port = port;
        this.interactions = new ArrayDeque<>(interactions);
        this.waitForConnectionClosed = new CountDownLatch(1);
        this.waitForAllExpectedCommands = new CountDownLatch(1);
    }

    public void start() throws IOException {
        ServerSocket serverSocket = new ServerSocket(port);
        mockServerThread = new MockServerThread(serverSocket, interactions, waitForConnectionClosed, waitForAllExpectedCommands);
        mockServerThread.start();
    }

    public void shutdown() throws InterruptedException {
        mockServerThread.shouldStop();
        mockServerThread.join();
    }

    private static class MockServerThread extends Thread {
        private final ServerSocket serverSocket;
        private final Deque<Pop3Interaction> interactions;
        private final CountDownLatch waitForConnectionClosed;
        private final CountDownLatch waitForAllExpectedCommands;
        private volatile boolean running = true;
        private Socket clientSocket;

        public MockServerThread(ServerSocket serverSocket, Deque<Pop3Interaction> interactions,
                              CountDownLatch waitForConnectionClosed, CountDownLatch waitForAllExpectedCommands) {
            this.serverSocket = serverSocket;
            this.interactions = interactions;
            this.waitForConnectionClosed = waitForConnectionClosed;
            this.waitForAllExpectedCommands = waitForAllExpectedCommands;
        }

        @Override
        public void run() {
            try {
                clientSocket = serverSocket.accept();
                PrintWriter writer = new PrintWriter(new OutputStreamWriter(clientSocket.getOutputStream()));
                BufferedReader reader = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));

                writer.println("+OK Mock POP3 server ready");
                writer.flush();

                handleInteractions(reader, writer);
                readAdditionalCommands(reader, writer);

                writer.close();
                reader.close();
                clientSocket.close();
                serverSocket.close();
            } catch (IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            } finally {
                waitForConnectionClosed.countDown();
            }
        }

        private void handleInteractions(BufferedReader reader, PrintWriter writer) throws IOException {
            while (!interactions.isEmpty() && running) {
                Pop3Interaction interaction = interactions.poll();
                String command = readLine(reader);
                if (command == null) {
                    break;
                }
                writer.println(interaction.getResponse());
                writer.flush();
            }
            waitForAllExpectedCommands.countDown();
        }

        private void readAdditionalCommands(BufferedReader reader, PrintWriter writer) throws IOException {
            while (running) {
                String command = readLine(reader);
                if (command == null) {
                    break;
                }
                writer.println("-ERR Unexpected command");
                writer.flush();
            }
        }

        private String readLine(BufferedReader reader) throws IOException {
            return reader.readLine();
        }

        public void shouldStop() {
            running = false;
            try {
                if (clientSocket != null && !clientSocket.isClosed()) {
                    clientSocket.close();
                }
                serverSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    public static class Pop3Interaction {
        private final String response;

        public Pop3Interaction(String response) {
            this.response = response;
        }

        public String getResponse() {
            return response;
        }
    }
}

