    private int skipLine(InputStream in) throws IOException {
        int c, count = 0;
        while ((c = in.read()) != -1) {
            count++;
            if (c == '\r') {
                in.read();
                count++;
                break;
            }
        }
        return count;
    }

    private void doReply(InputStream in, OutputStream out, String cmd) throws IOException, BadHttpRequest {
        if (cmd == null || cmd.length() == 0)
            throw new BadHttpRequest(400, "Bad Request");

        String[] parts = cmd.split(" ");
        if (parts.length < 2 || !parts[0].equals("GET"))
            throw new BadHttpRequest(400, "Bad Request");

        String path = parts[1];
        while (skipLine(in) > 0);

        if (path.endsWith(".class")) {
            if (!letUsersSendClassfile(out, path.substring(1), 0))
                throw new BadHttpRequest(404, "Not Found");
        } else {
            File file = new File(path.substring(1));
            if (!file.exists())
                throw new BadHttpRequest(404, "Not Found");

            try (FileInputStream fis = new FileInputStream(file)) {
                sendHeader(out, file.length(), 0);
                byte[] buf = new byte[4096];
                int len;
                while ((len = fis.read(buf)) > 0)
