            sendHeader(out, bytecode.length, 1);
            out.write(bytecode);
            return true;
        } catch (Exception e) {
            return false;
        }
    }

    private void sendHeader(OutputStream out, long dataLength, int filetype) throws IOException {
        String header = "HTTP/1.0 200 OK" + new String(endofline) +
                        "Content-Length: " + dataLength + new String(endofline) +
                        "Connection: close" + new String(endofline) +
                        (filetype == 1 ? "Content-Type: application/octet-stream" + new String(endofline) : "") +
                        new String(endofline);
        out.write(header.getBytes());
    }

    private void replyError(OutputStream out, BadHttpRequest e) throws IOException {
        String response = "HTTP/1.0 " + e.getCode() + " " + e.getMessage() + new String(endofline) +
                          "Connection: close" + new String(endofline) +
                          "Content-Type: text/html" + new String(endofline) +
                          new String(endofline) +
                          "<html><body><h1>" + e.getCode() + " " + e.getMessage() + "</h1></body></html>";
        out.write(response.getBytes());
    }
}

