    public static final int UPDATE_CONTENT = 1;
    public static final int UPDATE_PROGRESS = 2;
    public static final int GET_CURRENT_CONTENT = 3;
    public static final int SET_DRAW_DEBUG = 4;
    public static final int GET_LAYOUT_LIST = 5;
    public static final int GET_CURRENT_LAYOUT = 6;
    public static final int UPDATE_LAYOUT_DIMENSIONS = 7;
    private ExecutorService threadPool;

    public LinkServer() {
        this.port = 9999;
        init();
    }

    public LinkServer(int port) {
        this.port = port;
        init();
    }

    private void init() {
        try {
            server = new ServerSocket(port);
            threadPool = Executors.newCachedThreadPool();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void start() {
        new Thread(() -> {
            while (!server.isClosed()) {
                try {
                    Socket clientSocket = server.accept();
                    threadPool.execute(() -> handleRequest(clientSocket));
                } catch (IOException e) {
                    if (!server.isClosed()) {
                        e.printStackTrace();
                    }
                }
            }
        }).start();
    }

    private void handleRequest(Socket socket) {
        try (ObjectInputStream in = new ObjectInputStream(socket.getInputStream());
             ObjectOutputStream out = new ObjectOutputStream(socket.getOutputStream())) {
            int operation = in.readInt();
            switch (operation) {
                case UPDATE_CONTENT:
                    String content = (String) in.readObject();
                    break;
                case UPDATE_PROGRESS:
                    int progress = in.readInt();
                    break;
                case GET_CURRENT_CONTENT:
                    out.writeObject("Current Content");
                    break;
                case SET_DRAW_DEBUG:
                    boolean debug = in.readBoolean();
                    break;
                case GET_LAYOUT_LIST:
                    out.writeObject(new String[]{"Layout1", "Layout2"});
                    break;
                case GET_CURRENT_LAYOUT:
                    out.writeObject("Current Layout");
                    break;
                case UPDATE_LAYOUT_DIMENSIONS:
                    int width = in.readInt();
                    int height = in.readInt();
                    break;
                default:
                    out.writeObject("Invalid operation");
            }
            out.flush();
        } catch (IOException | ClassNotFoundException e) {
            e.printStackTrace();
        } finally {
            closeConnection(socket);
        }
    }

    private void closeConnection(Socket socket) {
        try {
            if (socket != null && !socket.isClosed()) {
                socket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

