        void handlePing(Socket socket) throws IOException {
            OutputStream outputStream = socket.getOutputStream();
            String response = "HTTP/1.1 200 OK\r\n\r\nPONG";
            outputStream.write(response.getBytes());
            outputStream.flush();
        }
    }
