import java.io.*;
import java.net.*;
import java.util.HashMap;

public abstract class EmbedHttpServer implements Runnable {
    private int port;
    private ServerSocket serverSocket;

    public EmbedHttpServer(int port) {
        this.port = port;
    }

    public void start() throws IOException {
        serverSocket = new ServerSocket(port);
        new Thread(this).start();
    }

    public void stop() throws IOException {
        if (serverSocket != null) {
            serverSocket.close();
        }
    }

    public abstract void handle(String method, String path, HashMap<String, String> headers, InputStream input, OutputStream response) throws IOException;

    public void run() {
        try {
            while (!serverSocket.isClosed()) {
                Socket clientSocket = serverSocket.accept();
                try {
                    BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
                    OutputStream out = clientSocket.getOutputStream();

                    String requestLine = in.readLine();
                    if (requestLine == null) continue;

                    String[] parts = requestLine.split(" ");
                    if (parts.length < 3) continue;

                    String method = parts[0];
                    String path = parts[1];

                    HashMap<String, String> headers = new HashMap<>();
                    String headerLine;
                    while ((headerLine = in.readLine()) != null && !headerLine.isEmpty()) {
                        int idx = headerLine.indexOf(':');
                        if (idx > 0) {
                            headers.put(headerLine.substring(0, idx).trim().toLowerCase(),
                                    headerLine.substring(idx + 1).trim());
                        }
                    }

                    int contentLength = 0;
                    if (headers.containsKey("content-length")) {
                        contentLength = Integer.parseInt(headers.get("content-length"));
                    }

                    InputStream bodyStream = new BodyInputStream(clientSocket.getInputStream(), contentLength);
                    handle(method, path, headers, bodyStream, out);

                    out.flush();
                } finally {
                    clientSocket.close();
                }
            }
        } catch (IOException e) {
            if (!serverSocket.isClosed()) {
                e.printStackTrace();
            }
        }
    }

    private static class BodyInputStream extends InputStream {
        private final InputStream in;
        private int remaining;

        public BodyInputStream(InputStream in, int length) {
            this.in = in;
            this.remaining = length;
        }

        public int available() throws IOException {
            return Math.min(in.available(), remaining);
        }

        public int read() throws IOException {
            if (remaining <= 0) return -1;
            int b = in.read();
            if (b != -1) remaining--;
            return b;
        }

        public int read(byte[] b, int off, int len) throws IOException {
            if (remaining <= 0) return -1;
            int toRead = Math.min(len, remaining);
            int read = in.read(b, off, toRead);
            if (read > 0) remaining -= read;
            return read;
        }

        public long skip(long n) throws IOException {
            throw new IOException("Skipping not supported");
        }

        public void close() throws IOException {
            in.close();
        }
    }
}

