import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.IOException;
import java.net.InetAddress;
import java.net.Socket;
import java.net.UnknownHostException;
import javax.mail.MessagingException;

public class Pop3Connection {
    private Socket socket;
    private BufferedInputStream in;
    private BufferedOutputStream out;

    public Socket connect(String host, int port) throws MessagingException {
        try {
            InetAddress[] addresses = InetAddress.getAllByName(host);
            for (InetAddress address : addresses) {
                try {
                    return connectToAddress(address, port);
                } catch (IOException e) {
                    continue;
                }
            }
            throw new MessagingException("Could not connect to any address for " + host);
        } catch (UnknownHostException e) {
            throw new MessagingException("Unknown host: " + host);
        }
    }

    private Socket connectToAddress(InetAddress address, int port) throws IOException {
        Socket socket = new Socket(address, port);
        socket.setSoTimeout(60000);
        return socket;
    }

    public void open(String host, int port) throws MessagingException {
        if (isOpen()) {
            return;
        }
        try {
            socket = connect(host, port);
            in = new BufferedInputStream(socket.getInputStream());
            out = new BufferedOutputStream(socket.getOutputStream());
        } catch (IOException e) {
            close();
            throw new MessagingException("Failed to open connection", e);
        }
    }

    public boolean isOpen() {
        return socket != null && socket.isConnected() && !socket.isClosed();
    }

    public void close() {
        try {
            if (out != null) {
                out.close();
            }
        } catch (IOException ignored) {
        } finally {
            out = null;
        }

        try {
            if (in != null) {
                in.close();
            }
        } catch (IOException ignored) {
        } finally {
            in = null;
        }

        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException ignored) {
        } finally {
            socket = null;
        }
    }

    public void writeLine(String s) throws IOException {
