        if (in == null) {
            throw new IOException("Connection not open");
        }
        StringBuilder sb = new StringBuilder();
        int c;
        while ((c = in.read()) != -1) {
            if (c == '\r') {
                c = in.read();
                if (c == '\n') {
                    break;
                }
                sb.append('\r');
            }
