    private Consumer<byte[]> on_rsp;

    public TcpRequester(SocketAddress dest, String request, String passcode, Consumer<byte[]> on_rsp) {
        this.dest = dest;
        this.request = request;
        this.passcode = passcode;
        this.on_rsp = on_rsp;
    }

    public TcpRequester start(InetAddress bind_addr, long timeout, int ttl) throws IOException {
        sock = new Socket();
        sock.bind(new InetSocketAddress(bind_addr, 0));
        sock.setSoTimeout((int) timeout);
        sock.setTrafficClass(ttl);
        sock.connect(dest);
        in = sock.getInputStream();
        out = sock.getOutputStream();
        return this;
    }

    public TcpRequester stop() throws IOException {
        if (sock != null) {
            sock.close();
            sock = null;
        }
        if (in != null) {
            in.close();
            in = null;
        }
        if (out != null) {
            out.close();
            out = null;
        }
        return this;
    }

    public boolean isRunning() {
        return sock != null && !sock.isClosed() && sock.isConnected();
    }

    public TcpRequester sendRequest(byte[] request) throws IOException {
        if (out == null) {
            throw new IOException("Connection not established");
        }
        out.write(request);
        out.flush();
        return this;
    }

    public byte[] fetchResponse() throws IOException {
        if (in == null) {
            throw new IOException("Connection not established");
        }
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        byte[] data = new byte[1024];
        int bytesRead;
