import java.io.BufferedInputStream;
import java.io.BufferedOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.Socket;
import java.util.concurrent.atomic.AtomicBoolean;

public final class MySQLChannel {
    private final DataSourceConfig dsc;
    private Socket socket;
    private InputStream in;
    private OutputStream out;
    private final AtomicBoolean isClosed = new AtomicBoolean(false);

    public MySQLChannel(DataSourceConfig dsc) {
        this.dsc = dsc;
    }

    public void connect(long timeout) throws IOException {
        socket = new Socket();
        socket.setSoTimeout((int) timeout);
        socket.setTcpNoDelay(true);
        socket.setKeepAlive(true);
        socket.connect(dsc.getSocketAddress(), (int) timeout);
        in = new BufferedInputStream(socket.getInputStream());
        out = new BufferedOutputStream(socket.getOutputStream());
    }

    public BinaryPacket execute(String statement) throws IOException {
        if (isClosed.get()) {
            throw new IOException("Channel is closed");
        }
        CommandPacket cmd = new CommandPacket(MySQLPacket.COM_QUERY, statement.getBytes());
        out.write(cmd.toBytes());
        out.flush();
        return receive();
    }

    public BinaryPacket receive() throws IOException {
        byte[] header = new byte[4];
        in.read(header);
        int packetLength = (header[0] & 0xff) + ((header[1] & 0xff) << 8) + ((header[2] & 0xff) << 16);
        byte packetId = header[3];
        byte[] packetData = new byte[packetLength];
        in.read(packetData);
        return new BinaryPacket(packetId, packetData);
    }

    public void close() throws IOException {
        if (isClosed.compareAndSet(false, true)) {
            try {
                out.write(QuitPacket.QUIT);
                out.flush();
            } finally {
                try {
                    in.close();
                } finally {
                    out.close();
                    socket.close();
                }
            }
        }
    }

    public static final class BinaryPacket {
        private final byte packetId;
        private final byte[] data;

        public BinaryPacket(byte packetId, byte[] data) {
            this.packetId = packetId;
            this.data = data;
        }

        public byte getPacketId() {
            return packetId;
        }

        public byte[] getData() {
            return data;
        }
    }

    public static final class CommandPacket {
        private final byte command;
        private final byte[] args;

        public CommandPacket(byte command, byte[] args) {
            this.command = command;
            this.args = args;
        }

        public byte[] toBytes() {
            byte[] bytes = new byte[1 + args.length];
            bytes[0] = command;
            System.arraycopy(args, 0, bytes, 1, args.length);
            return bytes;
        }
    }

    public static final class MySQLPacket {
        public static final byte COM_QUIT = 0x01;
        public static final byte COM_QUERY = 0x03;
    }

    public static final class QuitPacket {
        public static final byte[] QUIT = {MySQLPacket.COM_QUIT};
    }
}

