public class tcpConnection extends AsyncTask<String, Void, Void> {
    private Activity activity;
    private Context context;
    private String TAG = "TCPConnection";
    private static OutputStream out;
    private Socket socket;
    private boolean running = true;

    public tcpConnection(Activity activity, Context context) {
        this.activity = activity;
        this.context = context;
    }

    @Override
    protected Void doInBackground(String... strings) {
        String serverIP = strings[0];
        int serverPort = Integer.parseInt(strings[1]);

        while (running) {
            try {
                socket = new Socket();
                socket.connect(new InetSocketAddress(serverIP, serverPort), 5000);
                out = socket.getOutputStream();
                InputStream inputStream = socket.getInputStream();
                BufferedReader reader = new BufferedReader(new InputStreamReader(inputStream));

                String deviceInfo = android.os.Build.MODEL + ":" + android.os.Build.VERSION.RELEASE;
                PrintWriter writer = new PrintWriter(out, true);
                writer.println(deviceInfo);

                String command;
                while ((command = reader.readLine()) != null && running) {
                    if (command.equalsIgnoreCase("exit")) {
                        running = false;
                        break;
                    }
                    Log.d(TAG, "Received command: " + command);
                }
            } catch (SocketTimeoutException e) {
                Log.e(TAG, "Connection timeout: " + e.getMessage());
            } catch (IOException e) {
                Log.e(TAG, "Connection error: " + e.getMessage());
            } finally {
                try {
                    if (socket != null) {
                        socket.close();
                    }
                } catch (IOException e) {
                    Log.e(TAG, "Socket close error: " + e.getMessage());
                }
                if (running) {
                    try {
                        Thread.sleep(5000);
                    } catch (InterruptedException e) {
                        Log.e(TAG, "Sleep interrupted: " + e.getMessage());
                    }
                }
            }
        }
        return null;
    }

    public void stopConnection() {
        running = false;
        try {
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            Log.e(TAG, "Error stopping connection: " + e.getMessage());
        }
    }
}

