public class NetworkForwarder {
    private static final Logger LOGGER = Logger.getLogger(NetworkForwarder.class.getName());
    private final InetAddress destination;
    private final DatagramSocket connectionUdp;
    private final Map<InetSocketAddress, Socket> connectionsTcp;

    public NetworkForwarder(Config config) throws UnknownHostException, SocketException {
        this.destination = InetAddress.getByName(config.getDestinationHost());
        this.connectionUdp = new DatagramSocket();
        this.connectionsTcp = new HashMap<>();
    }

    public void forward(InetSocketAddress source, int port, boolean datagram, byte[] data) {
        if (datagram) {
            try {
                DatagramPacket packet = new DatagramPacket(data, data.length, destination, port);
                connectionUdp.send(packet);
            } catch (IOException e) {
                LOGGER.warning("Failed to forward UDP data: " + e.getMessage());
            }
        } else {
            try {
                Socket socket = connectionsTcp.computeIfAbsent(source, k -> {
                    try {
                        return new Socket(destination, port);
                    } catch (IOException e) {
                        LOGGER.warning("Failed to establish TCP connection: " + e.getMessage());
                        return null;
                    }
                });
                if (socket != null) {
                    OutputStream output = socket.getOutputStream();
                    output.write(data);
                    output.flush();
                }
            } catch (IOException e) {
                LOGGER.warning("Failed to forward TCP data: " + e.getMessage());
                connectionsTcp.remove(source);
            }
        }
    }

    public void disconnect(InetSocketAddress source) {
        Socket socket = connectionsTcp.remove(source);
        if (socket != null) {
            try {
                socket.close();
            } catch (IOException e) {
                LOGGER.warning("Failed to close TCP connection: " + e.getMessage());
            }
        }
    }
