public class UdpTransport {
    private DatagramSocket sock;
    private Thread receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private boolean server;
    private Log log;
    private List<SocketAddress> members = new CopyOnWriteArrayList<>();

    public interface RtReceiver {
        void receive(byte[] buf, int offset, int length, Object sender);
    }

    public void receiver(RtReceiver receiver) {
        this.receiver = receiver;
    }

    public Object localAddress() {
        return sock.getLocalSocketAddress();
    }

    public List<? extends Object> clusterMembers() {
        return Collections.unmodifiableList(members);
    }

    public void start(String... options) throws Exception {
        for (String option : options) {
            if (option.startsWith("port=")) {
                port = Integer.parseInt(option.substring(5));
            } else if (option.startsWith("host=")) {
                host = InetAddress.getByName(option.substring(5));
            } else if (option.equals("server")) {
                server = true;
            }
        }

        if (server) {
            sock = new DatagramSocket(port);
        } else {
            sock = new DatagramSocket();
        }

        receiver_thread = new Thread(new Receiver());
        receiver_thread.start();
    }

    public void stop() {
        if (receiver_thread != null) {
            receiver_thread.interrupt();
            try {
                receiver_thread.join();
            } catch (InterruptedException e) {}
            receiver_thread = null;
        }
        if (sock != null) {
            sock.close();
            sock = null;
        }
    }

    public void send(Object dest, byte[] buf, int offset, int length) throws Exception {
        SocketAddress addr = (SocketAddress) dest;
        DatagramPacket packet = new DatagramPacket(buf, offset, length, addr);
        sock.send(packet);
    }

    private class Receiver implements Runnable {
        public void run() {
            byte[] buf = new byte[65536];
            DatagramPacket packet = new DatagramPacket(buf, buf.length);

            while (!Thread.interrupted()) {
                try {
                    sock.receive(packet);
                    if (receiver != null) {
                        receiver.receive(packet.getData(), packet.getOffset(), packet.getLength(), packet.getSocketAddress());
                    }
                    if (server && !members.contains(packet.getSocketAddress())) {
                        members.add(packet.getSocketAddress());
                    }
                } catch (Exception e) {
                    if (!Thread.interrupted()) {
                        log.error("Error receiving packet", e);
                    }
                }
            }
        }
    }
}

