public class SntpImpl implements Sntp {
    private static final int NTP_PORT = 123;
    private static final int NTP_MODE = 3;
    private static final int NTP_VERSION = 3;
    private static final int NTP_PACKET_SIZE = 48;
    private static final long OFFSET_1900_TO_1970 = 2208988800L;

    @Override
    public SntpResult requestTime(InetAddress host, float roundTripDelayThreshold, float dispersionThreshold, int maxRetries, int timeout, SntpEventListener listener) {
        DatagramSocket socket = null;
        try {
            socket = new DatagramSocket();
            socket.setSoTimeout(timeout);

            byte[] buffer = new byte[NTP_PACKET_SIZE];
            DatagramPacket request = new DatagramPacket(buffer, buffer.length, host, NTP_PORT);
            DatagramPacket response = new DatagramPacket(buffer, buffer.length);

            writeNtpVersion(buffer);
            long requestTime = System.currentTimeMillis();
            writeTimeStamp(buffer, 40, (requestTime / 1000L) + OFFSET_1900_TO_1970);

            for (int attempt = 0; attempt < maxRetries; attempt++) {
                try {
                    socket.send(request);
                    socket.receive(response);

                    long originateTime = readTimeStamp(buffer, 24);
                    long receiveTime = readTimeStamp(buffer, 32);
                    long transmitTime = readTimeStamp(buffer, 40);
                    long responseTime = System.currentTimeMillis();

                    long roundTripDelay = (responseTime - requestTime) - (transmitTime - receiveTime);
                    double dispersion = doubleMillis(read(buffer, 4)) + doubleMillis(read(buffer, 8));

                    if (roundTripDelay < 0 || roundTripDelay > roundTripDelayThreshold * 1000) {
                        if (listener != null) {
                            listener.onRoundTripDelayExceeded(roundTripDelay);
                        }
                        continue;
                    }

                    if (dispersion > dispersionThreshold * 1000) {
                        if (listener != null) {
                            listener.onDispersionExceeded(dispersion);
                        }
                        continue;
                    }

                    long serverTime = receiveTime + (roundTripDelay / 2);
                    long offset = serverTime - ((requestTime + responseTime) / 2);

                    return new SntpResult(offset, roundTripDelay, dispersion);
                } catch (SocketTimeoutException e) {
                    if (listener != null) {
                        listener.onTimeout(attempt + 1);
                    }
                }
            }
        } catch (IOException e) {
            if (listener != null) {
                listener.onError(e);
            }
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
        return null;
    }

    private void writeNtpVersion(byte[] buffer) {
        buffer[0] = (byte) (NTP_MODE | (NTP_VERSION << 3));
    }

    private void writeTimeStamp(byte[] buffer, int offset, long time) {
        long seconds = time;
        long fraction = (long) ((time - seconds) * 4294967296.0);

        buffer[offset] = (byte) (seconds >> 24);
        buffer[offset + 1] = (byte) (seconds >> 16);
        buffer[offset + 2] = (byte) (seconds >> 8);
        buffer[offset + 3] = (byte) (seconds);

        buffer[offset + 4] = (byte) (fraction >> 24);
        buffer[offset + 5] = (byte) (fraction >> 16);
        buffer[offset + 6] = (byte) (fraction >> 8);
        buffer[offset + 7] = (byte) (fraction);
    }

    private long readTimeStamp(byte[] buffer, int offset) {
        long seconds = read(buffer, offset);
        long fraction = read(buffer, offset + 4);
        return (seconds - OFFSET_1900_TO_1970) * 1000 + ((fraction * 1000L) / 0x100000000L);
    }

    private long read(byte[] buffer, int offset) {
        return ((long) ui(buffer[offset]) << 24) |
               ((long) ui(buffer[offset + 1]) << 16) |
               ((long) ui(buffer[offset + 2]) << 8) |
               (long) ui(buffer[offset + 3]);
    }

    private int ui(byte b) {
        return b & 0xff;
    }

    private double doubleMillis(long fixedPoint) {
        return fixedPoint * 1000.0 / 0x100000000L;
    }
}

