import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetSocketAddress;
import java.net.SocketException;
import java.net.UnknownHostException;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.LinkedBlockingQueue;

public class Statsd {
    private static final Charset CHARSET = StandardCharsets.US_ASCII;
    private static final int BATCH_SIZE = 1000;
    private static final int BUFFER_SIZE = 1500;
    private static volatile Statsd instance;
    private final ExecutorService e = Executors.newSingleThreadExecutor();
    private final LinkedBlockingQueue<String> qManager = new LinkedBlockingQueue<>();
    private final DatagramSocket socket;
    private final InetSocketAddress address;

    private Statsd(String host, int port) throws SocketException, UnknownHostException {
        this.socket = new DatagramSocket();
        this.address = new InetSocketAddress(host, port);
        e.submit(this::backgroundThread);
    }

    public static synchronized Statsd getInstance(String host, int port) throws SocketException, UnknownHostException {
        if (instance == null) {
            instance = new Statsd(host, port);
        }
        return instance;
    }

    public void count(String metric, long value) {
        qManager.add(metric + ":" + value + "|c");
    }

    public void gauge(String metric, long value) {
        qManager.add(metric + ":" + value + "|g");
    }

    public void timing(String metric, long value) {
        qManager.add(metric + ":" + value + "|ms");
    }

    private void backgroundThread() {
        List<String> batch = new ArrayList<>(BATCH_SIZE);
        byte[] buffer = new byte[BUFFER_SIZE];
        int bufferPos = 0;

        while (!Thread.currentThread().isInterrupted()) {
            try {
                batch.clear();
                qManager.drainTo(batch, BATCH_SIZE);
                if (batch.isEmpty()) {
                    Thread.sleep(100);
                    continue;
                }

                bufferPos = 0;
                for (String metric : batch) {
                    byte[] metricBytes = metric.getBytes(CHARSET);
                    if (bufferPos + metricBytes.length + 1 > BUFFER_SIZE) {
                        sendPacket(buffer, bufferPos);
                        bufferPos = 0;
                    }
                    if (metricBytes.length > BUFFER_SIZE) {
                        continue;
                    }
                    System.arraycopy(metricBytes, 0, buffer, bufferPos, metricBytes.length);
                    bufferPos += metricBytes.length;
                    buffer[bufferPos++] = '\n';
                }

                if (bufferPos > 0) {
                    sendPacket(buffer, bufferPos);
                }
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            } catch (Exception e) {
                // Log or handle error
            }
        }
    }

    private void sendPacket(byte[] buffer, int length) {
        try {
            DatagramPacket packet = new DatagramPacket(buffer, length, address);
            socket.send(packet);
        } catch (IOException e) {
            // Log or handle error
        }
    }

    public void close() {
        e.shutdownNow();
        socket.close();
    }
}

