import java.io.IOException;
import java.net.DatagramPacket;
import java.net.DatagramSocket;
import java.net.InetSocketAddress;
import java.net.SocketAddress;
import java.net.SocketException;
import java.nio.ByteBuffer;
import java.nio.ByteOrder;
import java.time.Duration;
import java.time.Instant;
import java.util.Random;

public class SingleClientUdpTracker {
    private static final Duration CONNECTION_EXPIRATION_TIME = Duration.ofMinutes(1);
    private static final long EPHEMERAL_CONNECTION_ID = 0x41727101980L;
    private static final int CONNECT_CODE = 0;
    private static final int ANNOUNCE_CODE = 1;
    private static final int ERROR_CODE = 3;

    private final DatagramSocket serverSocket;
    private final int interval;
    private final int leechers;
    private final int seeders;
    private volatile boolean shutdown = false;
    private long currentConnection = 0;
    private long connectedOn = 0;

    public SingleClientUdpTracker(int interval, int leechers, int seeders) throws SocketException {
        this.serverSocket = new DatagramSocket(0);
        this.interval = interval;
        this.leechers = leechers;
        this.seeders = seeders;
    }

    public SocketAddress getServerAddress() {
        return serverSocket.getLocalSocketAddress();
    }

    public void run() throws IOException {
        byte[] buffer = new byte[65536];
        DatagramPacket packet = new DatagramPacket(buffer, buffer.length);

        while (!shutdown) {
            serverSocket.receive(packet);
            byte[] requestData = new byte[packet.getLength()];
            System.arraycopy(packet.getData(), packet.getOffset(), requestData, 0, packet.getLength());
            byte[] responseData = processRequest(requestData);
            if (responseData != null) {
                DatagramPacket responsePacket = new DatagramPacket(responseData, responseData.length, packet.getSocketAddress());
                serverSocket.send(responsePacket);
            }
        }
    }

    public byte[] processRequest(byte[] requestData) {
        if (requestData.length < 16) return null;

        ByteBuffer buffer = ByteBuffer.wrap(requestData).order(ByteOrder.BIG_ENDIAN);
        long connectionId = buffer.getLong();
        int action = buffer.getInt();
        int transactionId = buffer.getInt();

        if (action == CONNECT_CODE) {
            if (connectionId != EPHEMERAL_CONNECTION_ID) {
                return createError(transactionId, "Invalid connection ID");
            }
            currentConnection = generateConnectionId();
            connectedOn = System.currentTimeMillis();
            return createConnectResponse(currentConnection, transactionId);
        } else if (action == ANNOUNCE_CODE) {
            if (connectionId != currentConnection) {
                return createError(transactionId, "Invalid connection ID");
            }
            if (System.currentTimeMillis() - connectedOn > CONNECTION_EXPIRATION_TIME.toMillis()) {
                return createError(transactionId, "Connection expired");
            }
            return createAnnounceResponse(connectionId, transactionId);
        } else {
            return createError(transactionId, "Invalid action");
        }
    }

    private long generateConnectionId() {
        return new Random().nextLong();
    }

    private byte[] createConnectResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(16).order(ByteOrder.BIG_ENDIAN);
        buffer.putInt(0);
        buffer.putInt(transactionId);
        buffer.putLong(connectionId);
        return buffer.array();
    }

    private byte[] createAnnounceResponse(long connectionId, int transactionId) {
        ByteBuffer buffer = ByteBuffer.allocate(20).order(ByteOrder.BIG_ENDIAN);
        buffer.putInt(1);
        buffer.putInt(transactionId);
        buffer.putInt(interval);
        buffer.putInt(leechers);
        buffer.putInt(seeders);
        return buffer.array();
    }

    private byte[] createError(int transactionId, String message) {
        byte[] messageBytes = message.getBytes();
        ByteBuffer buffer = ByteBuffer.allocate(8 + messageBytes.length).order(ByteOrder.BIG_ENDIAN);
        buffer.putInt(ERROR_CODE);
        buffer.putInt(transactionId);
        buffer.put(messageBytes);
        return buffer.array();
    }

    public void shutdown() {
        shutdown = true;
        serverSocket.close();
    }
}

