import java.net.*;
import java.nio.ByteBuffer;
import java.util.*;
import java.util.logging.Logger;

public class UDPTrackerClient {
    private final Logger logger;
    private static final int UDP_BASE_TIMEOUT_SECONDS = 15;
    private static final int UDP_MAX_TRIES = 8;
    private static final int UDP_MAX_TRIES_ON_STOPPED = 3;
    private static final int UDP_PACKET_LENGTH = 8192;
    private final InetSocketAddress address;
    private final Random random;
    private DatagramSocket socket;
    private Date connectionExpiration;
    private long connectionId;
    private int transactionId;
    private boolean stop;

    public UDPTrackerClient(List<Peer> peers, URI tracker) {
        this.logger = Logger.getLogger(UDPTrackerClient.class.getName());
        this.random = new Random();
        this.address = new InetSocketAddress(tracker.getHost(), tracker.getPort());
        try {
            this.socket = new DatagramSocket();
            this.socket.setSoTimeout(UDP_BASE_TIMEOUT_SECONDS * 1000);
        } catch (SocketException e) {
            logger.severe("Failed to create UDP socket: " + e.getMessage());
        }
    }

    public void announce(AnnounceRequestMessage.RequestEvent event, boolean inhibitEvents, AnnounceableInformation torrent, List<Peer> peers) {
        if (stop) return;
        try {
            if (connectionExpiration == null || connectionExpiration.before(new Date())) {
                connect();
            }
            for (Peer peer : peers) {
                UDPAnnounceRequestMessage request = buildAnnounceRequest(event, torrent, peer);
                send(request.getData());
                ByteBuffer response = recv(0);
                TrackerMessage message = TrackerMessage.parse(response);
                validateTrackerResponse(message);
                handleTrackerAnnounceResponse(message, inhibitEvents, torrent.getHexInfoHash());
            }
        } catch (Exception e) {
            logger.warning("Announce failed: " + e.getMessage());
        }
    }

    private void handleTrackerAnnounceResponse(TrackerMessage message, boolean inhibitEvents, String hexInfoHash) {
        if (message instanceof UDPAnnounceResponseMessage) {
            UDPAnnounceResponseMessage response = (UDPAnnounceResponseMessage) message;
            if (!inhibitEvents) {
                logger.info("Announce response: " + response.getInterval() + " peers: " + response.getPeers().size());
            }
        }
    }

    public void close() {
        stop = true;
        if (socket != null && !socket.isClosed()) {
            socket.close();
        }
    }

    private UDPAnnounceRequestMessage buildAnnounceRequest(AnnounceRequestMessage.RequestEvent event, AnnounceableInformation torrent, Peer peer) {
        transactionId = random.nextInt();
        return new UDPAnnounceRequestMessage(connectionId, transactionId, torrent.getInfoHash(), peer.getId(), 
            peer.getPort(), event, 0, 0, torrent.getUploaded(), torrent.getDownloaded(), torrent.getLeft(), 1);
    }

    private void validateTrackerResponse(TrackerMessage message) {
        if (message.getTransactionId() != transactionId) {
            throw new IllegalStateException("Transaction ID mismatch");
        }
    }

    private void handleTrackerConnectResponse(TrackerMessage message) {
        if (message instanceof UDPConnectResponseMessage) {
            UDPConnectResponseMessage response = (UDPConnectResponseMessage) message;
            connectionId = response.getConnectionId();
            connectionExpiration = new Date(System.currentTimeMillis() + (response.getInterval() * 1000L));
        }
    }

    private void send(ByteBuffer data) {
        try {
            byte[] bytes = new byte[data.remaining()];
            data.get(bytes);
            DatagramPacket packet = new DatagramPacket(bytes, bytes.length, address);
            socket.send(packet);
        } catch (Exception e) {
            logger.severe("Failed to send UDP packet: " + e.getMessage());
        }
    }

    private ByteBuffer recv(int attempt) {
        if (attempt >= UDP_MAX_TRIES) {
            throw new IllegalStateException("Max retries reached");
        }
        try {
            byte[] buffer = new byte[UDP_PACKET_LENGTH];
            DatagramPacket packet = new DatagramPacket(buffer, buffer.length);
            socket.receive(packet);
            return ByteBuffer.wrap(packet.getData(), 0, packet.getLength());
        } catch (SocketTimeoutException e) {
            return recv(attempt + 1);
        } catch (Exception e) {
            logger.severe("Failed to receive UDP packet: " + e.getMessage());
            throw new IllegalStateException("Receive failed", e);
        }
    }

    private void connect() {
