import java.net.*;
import java.io.*;
import java.util.*;
import java.nio.*;

public class DnsUdpClient {
    public static final InetSocketAddress[] DNS_SERVERS = {
        new InetSocketAddress("8.8.8.8", 53),
        new InetSocketAddress("8.8.4.4", 53),
        new InetSocketAddress("1.1.1.1", 53)
    };

    public List<Inet4Address> resolveAOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.setSoTimeout(5000);
        DatagramPacket packet = new DatagramPacket(query, query.length, dnsServer);
        socket.send(packet);

        byte[] buffer = new byte[1024];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);
        socket.close();

        return toIps(buffer);
    }

    public List<Inet4Address> toIps(byte[] buffer) {
        List<Inet4Address> ips = new ArrayList<>();
        ByteBuffer bb = ByteBuffer.wrap(buffer);
        bb.position(6);
        short answerCount = bb.getShort();

        bb.position(12);
        while ((bb.get() & 0xFF) != 0);

        for (int i = 0; i < answerCount; i++) {
            bb.position(bb.position() + 10);
            short rdLength = bb.getShort();
            if (rdLength == 4) {
                byte[] ipBytes = new byte[4];
                bb.get(ipBytes);
                try {
                    ips.add((Inet4Address) Inet4Address.getByAddress(ipBytes));
                } catch (UnknownHostException e) {
                    continue;
                }
            } else {
                bb.position(bb.position() + rdLength);
            }
        }
        return ips;
    }

    public String resolveTXTOverUdp(InetSocketAddress dnsServer, byte[] query) throws Exception {
        DatagramSocket socket = new DatagramSocket();
        socket.setSoTimeout(5000);
        DatagramPacket packet = new DatagramPacket(query, query.length, dnsServer);
        socket.send(packet);

        byte[] buffer = new byte[1024];
        DatagramPacket response = new DatagramPacket(buffer, buffer.length);
        socket.receive(response);
        socket.close();

        return toTXT(buffer);
    }

    public String toTXT(byte[] buffer) {
        ByteBuffer bb = ByteBuffer.wrap(buffer);
        bb.position(6);
        short answerCount = bb.getShort();

        bb.position(12);
        while ((bb.get() & 0xFF) != 0);

        for (int i = 0; i < answerCount; i++) {
            bb.position(bb.position() + 10);
            short rdLength = bb.getShort();
            if (bb.get() == (byte) 0xC0) {
                bb.get();
                continue;
            }
            bb.position(bb.position() - 1);
            int txtLength = bb.get() & 0xFF;
            byte[] txtBytes = new byte[txtLength];
            bb.get(txtBytes);
            return new String(txtBytes);
        }
        throw new RuntimeException("No TXT record found");
    }
}

