public class KafkaTopicCreator {
    public static boolean createTopic(KafkaClientInfo kafkaClientInfo, NewTopicInfo newTopicInfo) {
        Properties props = new Properties();
        props.put(AdminClientConfig.BOOTSTRAP_SERVERS_CONFIG, kafkaClientInfo.getBootstrapServers());
        if (kafkaClientInfo.getSecurityProtocol() != null) {
            props.put(AdminClientConfig.SECURITY_PROTOCOL_CONFIG, kafkaClientInfo.getSecurityProtocol());
        }
        if (kafkaClientInfo.getSaslMechanism() != null) {
            props.put("sasl.mechanism", kafkaClientInfo.getSaslMechanism());
        }
        if (kafkaClientInfo.getSaslJaasConfig() != null) {
            props.put("sasl.jaas.config", kafkaClientInfo.getSaslJaasConfig());
        }

        try (AdminClient adminClient = AdminClient.create(props)) {
            Map<String, String> topicConfigs = new HashMap<>();
            if (newTopicInfo.getRetentionTimeMs() > 0) {
                topicConfigs.put(TopicConfig.RETENTION_MS_CONFIG, String.valueOf(newTopicInfo.getRetentionTimeMs()));
            }

            NewTopic newTopic = new NewTopic(
                newTopicInfo.getTopicName(),
                newTopicInfo.getNumPartitions(),
                newTopicInfo.getReplicationFactor()
            ).configs(topicConfigs);

            adminClient.createTopics(Collections.singleton(newTopic)).all().get();
            return true;
        } catch (InterruptedException | ExecutionException e) {
            Thread.currentThread().interrupt();
            return false;
        } catch (Exception e) {
            return false;
        }
    }
