import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import java.util.Map;
import com.google.gson.JsonObject;

public class HttpUtils {
    public static String parseUrlArgs(String responseUrl, JsonObject jsonObject) {
        if (responseUrl == null || responseUrl.isEmpty()) {
            return responseUrl;
        }

        int queryIndex = responseUrl.indexOf('?');
        if (queryIndex == -1) {
            return responseUrl;
        }

        String baseUrl = responseUrl.substring(0, queryIndex);
        String query = responseUrl.substring(queryIndex + 1);
        String[] pairs = query.split("&");

        for (String pair : pairs) {
            int idx = pair.indexOf('=');
            if (idx > 0) {
                String key = pair.substring(0, idx);
                String value = pair.substring(idx + 1);
                jsonObject.addProperty(key, value);
            }
        }

        return baseUrl;
    }

    public static String post(String httpUrl, String content) throws Exception {
        URL url = new URL(httpUrl);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestMethod("POST");
        connection.setDoOutput(true);
        connection.setRequestProperty("Content-Type", "application/json");
        connection.setRequestProperty("Accept", "application/json");

        if (content != null && !content.isEmpty()) {
            try (OutputStream os = connection.getOutputStream()) {
                byte[] input = content.getBytes("utf-8");
                os.write(input, 0, input.length);
            }
        }

        int responseCode = connection.getResponseCode();
        if (responseCode != HttpURLConnection.HTTP_OK) {
            throw new Exception("HTTP request failed with response code: " + responseCode);
        }

        StringBuilder response = new StringBuilder();
        try (BufferedReader br = new BufferedReader(new InputStreamReader(connection.getInputStream(), "utf-8"))) {
            String responseLine;
            while ((responseLine = br.readLine()) != null) {
                response.append(responseLine.trim());
            }
        }

        connection.disconnect();
        return response.toString();
    }
}

