import java.io.BufferedOutputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.Proxy;
import java.net.URL;
import java.util.Map;
import java.util.zip.GZIPOutputStream;
import javax.net.ssl.HttpsURLConnection;
import javax.net.ssl.SSLSocketFactory;

public class HttpService {
    private boolean shouldGzipRequestPayload;
    private static final int MIN_UNAVAILABLE_HTTP_RESPONSE_CODE = 500;
    private static final int MAX_UNAVAILABLE_HTTP_RESPONSE_CODE = 599;
    private static final String LOGTAG = "HttpService";
    private static final int HTTP_OUTPUT_STREAM_BUFFER_SIZE = 8192;
    private static final String CONTENT_ENCODING_HEADER = "Content-Encoding";
    private static final String GZIP_CONTENT_TYPE_HEADER = "gzip";

    public byte[] performRequest(String endpointUrl, ProxyServerInteractor interactor, Map<String, Object> params, SSLSocketFactory socketFactory) throws IOException, ServiceUnavailableException {
        HttpURLConnection connection = null;
        try {
            URL url = new URL(endpointUrl);
            if (isProxyRequest(endpointUrl)) {
                Proxy proxy = interactor.getProxy();
                connection = (HttpURLConnection) url.openConnection(proxy);
            } else {
                connection = (HttpURLConnection) url.openConnection();
            }

            if (connection instanceof HttpsURLConnection && socketFactory != null) {
                ((HttpsURLConnection) connection).setSSLSocketFactory(socketFactory);
            }

            connection.setRequestMethod("POST");
            connection.setDoOutput(true);
            connection.setDoInput(true);

            if (shouldGzipRequestPayload) {
                connection.setRequestProperty(CONTENT_ENCODING_HEADER, GZIP_CONTENT_TYPE_HEADER);
            }

            OutputStream out = getBufferedOutputStream(connection.getOutputStream());
            if (params != null) {
                out.write(params.toString().getBytes());
            }
            out.close();

            int responseCode = connection.getResponseCode();
            if (responseCode >= MIN_UNAVAILABLE_HTTP_RESPONSE_CODE && responseCode <= MAX_UNAVAILABLE_HTTP_RESPONSE_CODE) {
                throw new ServiceUnavailableException("Server returned HTTP response code: " + responseCode);
            }

            if (responseCode != HttpURLConnection.HTTP_OK) {
                throw new IOException("Server returned HTTP response code: " + responseCode);
            }

            return slurp(connection.getInputStream());
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }

    private OutputStream getBufferedOutputStream(OutputStream out) throws IOException {
        if (shouldGzipRequestPayload) {
            return new GZIPOutputStream(new BufferedOutputStream(out, HTTP_OUTPUT_STREAM_BUFFER_SIZE));
        }
        return new BufferedOutputStream(out, HTTP_OUTPUT_STREAM_BUFFER_SIZE);
    }

    private boolean isProxyRequest(String endpointUrl) {
        return endpointUrl != null && endpointUrl.startsWith("http://proxy.");
    }

    private byte[] slurp(InputStream inputStream) throws IOException {
        ByteArrayOutputStream buffer = new ByteArrayOutputStream();
        byte[] data = new byte[HTTP_OUTPUT_STREAM_BUFFER_SIZE];
        int bytesRead;
        while ((bytesRead = inputStream.read(data, 0, data.length)) != -1) {
            buffer.write(data, 0, bytesRead);
        }
        buffer.flush();
        return buffer.toByteArray();
    }
}

