            long date = connection.getDate();
            if (date <= 0) {
                date = -1L;
            }
            if (callback != null) {
                callback.onResponse(date);
            }
        } catch (Exception e) {
            if (callback != null) {
                callback.onFail(e);
            }
        } finally {
            if (connection != null) {
                try {
                    connection.disconnect();
                } catch (Exception ignore) {}
            }
        }
    }
