import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class HttpURLConnectionUtils {
    private static final String TAG = "HttpURLConnectionUtils";
    private static final int TIMEOUT_IN_MILLIONS = 5000;
    private static final String BAIDU_URL = "https://www.baidu.com";
    private static final ExecutorService executor = Executors.newCachedThreadPool();

    public interface Callback {
        void onResponse(String result, long responseTime);
        void onFail(Throwable error);
    }

    public interface TimeCallback {
        void onResponse(long millis);
        void onFail(Throwable error);
    }

    public static void doGetAsync(String urlStr, Callback callback) {
        request("GET", urlStr, null, null, callback);
    }

    public static void doPostAsync(String urlStr, String params, Callback callback) {
        request("POST", urlStr, null, params, callback);
    }

    public static void getNetTime(TimeCallback callback) {
        reqNetTime(BAIDU_URL, callback);
    }

    public static void getNetTime(String urlStr, TimeCallback callback) {
        reqNetTime(urlStr, callback);
    }

    private static void request(final String method, final String urlStr, final Map<String, String> headers, final String params, final Callback callback) {
        executor.execute(() -> {
            HttpURLConnection conn = null;
            long startTime = System.currentTimeMillis();
            try {
                URL url = new URL(urlStr);
                conn = (HttpURLConnection) url.openConnection();
                conn.setRequestMethod(method);
                conn.setConnectTimeout(TIMEOUT_IN_MILLIONS);
                conn.setReadTimeout(TIMEOUT_IN_MILLIONS);
                conn.setUseCaches(false);

                if (headers != null) {
                    for (Map.Entry<String, String> entry : headers.entrySet()) {
                        conn.setRequestProperty(entry.getKey(), entry.getValue());
                    }
                }

                if ("POST".equals(method) {
                    conn.setDoOutput(true);
                    conn.setRequestProperty("Content-Type", "application/x-www-form-urlencoded");
                    if (params != null) {
                        OutputStream os = conn.getOutputStream();
                        os.write(params.getBytes());
                        os.flush();
                        os.close();
                    }
                }

                int responseCode = conn.getResponseCode();
                if (responseCode == HttpURLConnection.HTTP_OK) {
                    InputStream is = conn.getInputStream();
                    BufferedReader reader = new BufferedReader(new InputStreamReader(is));
                    StringBuilder response = new StringBuilder();
                    String line;
                    while ((line = reader.readLine()) != null) {
                        response.append(line);
                    }
                    reader.close();
                    is.close();
                    long responseTime = System.currentTimeMillis() - startTime;
                    callback.onResponse(response.toString(), responseTime);
                } else {
                    throw new Exception("HTTP error code: " + responseCode);
                }
            } catch (Exception e) {
                callback.onFail(e);
            } finally {
                if (conn != null) {
                    conn.disconnect();
                }
            }
        });
    }

    private static void reqNetTime(final String urlStr, final TimeCallback callback) {
        executor.execute(() -> {
            HttpURLConnection conn = null;
            try {
                URL url = new URL(urlStr);
                conn = (HttpURLConnection) url.openConnection();
                conn.setRequestMethod("GET");
                conn.setConnectTimeout(TIMEOUT_IN_MILLIONS);
                conn.setReadTimeout(TIMEOUT_IN_MILLIONS);
                conn.setUseCaches(false);

                long serverTime = conn.getDate();
                if (serverTime > 0) {
                    callback.onResponse(serverTime);
                } else {
                    throw new Exception("Failed to get network time");
                }
            } catch (Exception e) {
                callback.onFail(e);
            } finally {
                if (conn != null) {
                    conn.disconnect();
                }
            }
        });
    }
}

