import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.InetSocketAddress;
import java.net.Proxy;
import java.net.URL;
import java.util.Map;
import javax.net.ssl.HttpsURLConnection;
import javax.net.ssl.SSLContext;
import javax.net.ssl.TrustManager;
import javax.net.ssl.X509TrustManager;

public class HttpClient {
    private static Config config = Config.getInstance();

    public static String sendRequest(HttpRequestInfo httpRequestInfo, String cookieValue) {
        HttpURLConnection connection = null;
        InputStream inputStream = null;
        ByteArrayOutputStream baos = null;
        try {
            connection = createConnection(httpRequestInfo);
            configureRequest(connection, httpRequestInfo, cookieValue);
            sendRequestBody(connection, httpRequestInfo);
            inputStream = connection.getInputStream();
            baos = new ByteArrayOutputStream();
            byte[] buffer = new byte[1024];
            int length;
            while ((length = inputStream.read(buffer)) != -1) {
                baos.write(buffer, 0, length);
            }
            return baos.toString("UTF-8");
        } catch (Exception e) {
            return null;
        } finally {
            closeResources(connection, inputStream, baos);
        }
    }

    private static HttpURLConnection createConnection(HttpRequestInfo httpRequestInfo) throws Exception {
        URL url = new URL(httpRequestInfo.getUrl());
        HttpURLConnection connection;
        if (config.getProxyHost() != null && config.getProxyPort() != 0) {
            Proxy proxy = new Proxy(Proxy.Type.HTTP, new InetSocketAddress(config.getProxyHost(), config.getProxyPort()));
            connection = (HttpURLConnection) url.openConnection(proxy);
        } else {
            connection = (HttpURLConnection) url.openConnection();
        }
        if (connection instanceof HttpsURLConnection) {
            HttpsURLConnection httpsConnection = (HttpsURLConnection) connection;
            if (!config.isSslVerificationEnabled()) {
                SSLContext sslContext = SSLContext.getInstance("TLS");
                sslContext.init(null, new TrustManager[]{new X509TrustManager() {
                    public java.security.cert.X509Certificate[] getAcceptedIssuers() { return null; }
                    public void checkClientTrusted(java.security.cert.X509Certificate[] certs, String authType) {}
                    public void checkServerTrusted(java.security.cert.X509Certificate[] certs, String authType) {}
                }}, new java.security.SecureRandom());
                httpsConnection.setSSLSocketFactory(sslContext.getSocketFactory());
                httpsConnection.setHostnameVerifier((hostname, session) -> true);
            }
        }
        connection.setConnectTimeout(config.getConnectionTimeout());
        connection.setReadTimeout(config.getReadTimeout());
        return connection;
    }

    private static void configureRequest(HttpURLConnection connection, HttpRequestInfo httpRequestInfo, String cookieValue) {
        connection.setRequestMethod(httpRequestInfo.getMethod());
        Map<String, String> headers = httpRequestInfo.getHeaders();
        if (cookieValue != null && !cookieValue.isEmpty()) {
            configureCookie(headers, cookieValue);
        }
        for (Map.Entry<String, String> entry : headers.entrySet()) {
            connection.setRequestProperty(entry.getKey(), entry.getValue());
        }
    }

    private static void configureCookie(Map<String, String> headers, String cookieValue) {
        headers.put("Cookie", cookieValue);
    }

    private static void sendRequestBody(HttpURLConnection connection, HttpRequestInfo httpRequestInfo) throws IOException {
        if (httpRequestInfo.getBody() != null && !httpRequestInfo.getBody().isEmpty()) {
            connection.setDoOutput(true);
            try (OutputStream outputStream = connection.getOutputStream()) {
                outputStream.write(httpRequestInfo.getBody().getBytes("UTF-8"));
                outputStream.flush();
            }
        }
    }

    private static void closeResources(HttpURLConnection connection, InputStream inputStream, ByteArrayOutputStream baos) {
        try {
            if (inputStream != null) {
                inputStream.close();
            }
        } catch (IOException e) {}
        try {
            if (baos != null) {
                baos.close();
            }
        } catch (IOException e) {}
        if (connection != null) {
            connection.disconnect();
        }
    }
}
