public class FileChannelProcessor {
    private ConcurrentHashMap<ByteBuffer, Integer> mins;
    private ConcurrentHashMap<ByteBuffer, Integer> maxs;
    private ConcurrentHashMap<ByteBuffer, Integer> sums;
    private ConcurrentHashMap<ByteBuffer, Integer> counts;

    public FileChannelProcessor() {
        mins = new ConcurrentHashMap<>();
        maxs = new ConcurrentHashMap<>();
        sums = new ConcurrentHashMap<>();
        counts = new ConcurrentHashMap<>();
    }

    public void processChunk(MappedByteBuffer chunk, long chunkSize) {
        while (chunk.hasRemaining()) {
            int value = chunk.getInt();
            ByteBuffer key = ByteBuffer.allocate(4);
            key.putInt(value);
            key.flip();

            mins.compute(key, (k, v) -> (v == null || value < v) ? value : v);
            maxs.compute(key, (k, v) -> (v == null || value > v) ? value : v);
            sums.compute(key, (k, v) -> (v == null) ? value : v + value);
            counts.compute(key, (k, v) -> (v == null) ? 1 : v + 1);
        }
    }

    public void processFile(Path filePath) throws IOException {
        try (FileChannel channel = FileChannel.open(filePath, StandardOpenOption.READ)) {
            long fileSize = channel.size();
            long chunkSize = Math.min(1024 * 1024, fileSize); // Process in chunks of 1MB or smaller
            long position = 0;

            while (position < fileSize) {
                long size = Math.min(chunkSize, fileSize - position);
                MappedByteBuffer buffer = channel.map(FileChannel.MapMode.READ_ONLY, position, size);
                processChunk(buffer, size);
                position += size;
            }
        }
    }
}
