public class GeyserServer {
    public static final Transport TRANSPORT = compatibleTransport();
    public static final int SHUTDOWN_QUIET_PERIOD_MS = 10000;
    public static final int SHUTDOWN_TIMEOUT_MS = 60000;

    private final GeyserImpl geyser;
    private final EventLoopGroup group;
    private final EventLoopGroup childGroup;
    private final ServerBootstrap bootstrap;
    private final EventLoopGroup playerGroup;
    private final ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = createEventLoopGroup(threadCount);
        this.childGroup = createEventLoopGroup(threadCount);
        this.playerGroup = createEventLoopGroup(threadCount);
        this.bootstrap = createBootstrap();
        this.bootstrapFutures = new ChannelFuture[1];
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        CompletableFuture<Void> future = new CompletableFuture<>();
        ChannelFuture bindFuture = bootstrap.bind(address);
        bindFuture.addListener((ChannelFutureListener) f -> {
            if (f.isSuccess()) {
                bootstrapFutures[0] = f;
                future.complete(null);
            } else {
                future.completeExceptionally(f.cause());
            }
        });
        return future;
    }

    public void shutdown() {
        final EventExecutorGroup executor = group.next();
        executor.submit(() -> {
            try {
                group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, true);
                childGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, true);
                playerGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, true);
            } finally {
                executor.shutdownGracefully();
            }
        });
    }

    private ServerBootstrap createBootstrap() {
        ServerBootstrap b = new ServerBootstrap();
        b.group(group, childGroup)
         .channel(TRANSPORT.channelClass())
         .childHandler(geyser);
        return b;
    }

    private static Transport compatibleTransport() {
        if (isClassAvailable("io.netty.channel.epoll.Epoll")) {
            return new Transport(EpollServerSocketChannel.class, EpollEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.kqueue.KQueue")) {
            return new Transport(KQueueServerSocketChannel.class, KQueueEventLoopGroup::new);
        } else if (isClassAvailable("io.netty.channel.uring.IOUring")) {
            return new Transport(IOUringServerSocketChannel.class, IOUringEventLoopGroup::new);
        } else {
            return new Transport(NioServerSocketChannel.class, NioEventLoopGroup::new);
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className);
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    private EventLoopGroup createEventLoopGroup(int threadCount) {
        switch (TRANSPORT) {
            case EPOLL:
                return new EpollEventLoopGroup(threadCount);
            case KQUEUE:
                return new KQueueEventLoopGroup(threadCount);
            case IOURING:
                return new IOUringEventLoopGroup(threadCount);
            default:
                return new NioEventLoopGroup(threadCount);
        }
    }

    private static record Transport(Class<? extends SocketChannel> channelClass, Supplier<EventLoopGroup> eventLoopGroupFactory) {}
}
