import org.apache.kafka.clients.producer.Producer;
import org.apache.kafka.clients.producer.ProducerConfig;
import org.apache.kafka.clients.producer.ProducerRecord;
import org.apache.kafka.common.serialization.StringSerializer;
import java.util.Properties;

public class MaintenancePlanProducer {
    private Producer<String, MaintenancePlan> _producer;

    public MaintenancePlanProducer(String bootstrapServers, Properties overrides) {
        Properties props = new Properties();
        props.setProperty(ProducerConfig.BOOTSTRAP_SERVERS_CONFIG, bootstrapServers);
        props.setProperty(ProducerConfig.KEY_SERIALIZER_CLASS_CONFIG, StringSerializer.class.getCanonicalName());
        props.setProperty(ProducerConfig.VALUE_SERIALIZER_CLASS_CONFIG, MaintenancePlanSerde.class.getCanonicalName());

        if (overrides != null) {
            props.putAll(overrides);
        }

        _producer = new KafkaProducer<>(props);
    }

    public void sendPlan(String topic, MaintenancePlan maintenancePlan) {
        _producer.send(new ProducerRecord<>(topic, maintenancePlan), (recordMetadata, e) -> {
            if (e != null) {
                throw new RuntimeException("Failed to produce maintenance plan", e);
            }
        });
    }

    public void close() {
        _producer.close();
    }
}

