        this.buffer = channel.map(FileChannel.MapMode.READ_WRITE, 0, MAX_LENGTH);
        this.size = MAX_LENGTH;
        read();
    }

    @Override
    public void close() throws IOException {
        if (channel != null) {
            channel.close();
        }
    }

    public List<DaemonInfo> getAll() {
        synchronized (lck) {
            return new ArrayList<>(infosMap.values());
        }
    }

    public void read() {
        synchronized (lck) {
            buffer.rewind();
            while (buffer.hasRemaining()) {
                String key = readString();
                DaemonInfo info = readDaemonInfo();
                infosMap.put(key, info);
            }
        }
    }

    public void doUpdate(Runnable updater) {
        FileLock lock = tryLock();
        try {
            updater.run();
        } finally {
            if (lock != null) {
                lock.release();
            }
        }
    }

    private FileLock tryLock() {
        Object lockObj = locks.computeIfAbsent(registryFile, k -> new ReentrantLock());
        ReentrantLock lock = (ReentrantLock) lockObj;
        return lock.tryLock(LOCK_TIMEOUT_MS, TimeUnit.MILLISECONDS);
    }

    public void reset() {
        synchronized (lck) {
            infosMap.clear();
            buffer.clear();
            size = MAX_LENGTH;
        }
    }

    private String readString() {
        byte[] bytes = new byte[buffer.getInt()];
        buffer.get(bytes);
        return new String(bytes, StandardCharsets.UTF_8);
    }

    private void writeString(String str) {
        byte[] bytes = str.getBytes(StandardCharsets.UTF_8);
        buffer.putInt(bytes.length);
        buffer.put(bytes);
