                .socketAddressResolver(MappingSocketAddressResolver.create(DnsResolver.jvmDefault(), hostAndPort -> {
                    return HostAndPort.of(hostAndPort.getHostText(), hostAndPort.getPort() + 443);
                })).build();
    }

    public StatefulRedisSentinelConnection<String, String> connectToSentinelDirectly(String host) {
        RedisURI redisURI = RedisURI.create("rediss://" + host + ":" + RedisURI.DEFAULT_SENTINEL_PORT);
        redisURI.setVerifyPeer(false);

        RedisClient client = RedisClient.create(clientResources);
        StatefulRedisSentinelConnection<String, String> connection = client.connectSentinel(redisURI);
        return connection;
    }

    public StatefulRedisConnection<String, String> connectToMasterViaSentinel(String host, String masterId, File truststore) {
        RedisURI redisURI = RedisURI.create("rediss-sentinel://" + host + ":" + RedisURI.DEFAULT_SENTINEL_PORT
                + "?sentinelMasterId=" + masterId);
        SslOptions options = SslOptions.builder().truststore(truststore).build();

        RedisClient client = RedisClient.create(clientResources);
        client.setOptions(ClientOptions.builder().sslOptions(options).build());
        StatefulRedisConnection<String, String> connection = client.connect(redisURI);
        return connection;
    }

