import io.lettuce.core.publisher.RedisPublisher;
import io.lettuce.core.api.sync.RedisCommands;

public class RedisInteraction {
    private RedisClient client;
    private StatefulRedisConnection<String, String> connection;

    public void initializeConnection() {
        client = RedisClient.create("redis://localhost:6379");
        connection = client.connect();
        RedisCommands<String, String> commands = connection.sync();
        commands.flushall();
    }

    public void closeConnection() {
        connection.close();
        client.shutdown();
    }

    public RedisPublisher<String> createRedisPublisher(long elements) {
        if (elements == Long.MAX_VALUE) {
            return null;
        }
        RedisCommands<String, String> commands = connection.sync();
        for (long i = 0; i < elements; i++) {
            commands.set("key" + i, "value" + i);
        }
        return connection publisher();
    }
}
