        if (contentLength == null) {
            exchange.getResponseHeaders().add("Content-Length", "0");
            callback.onWriteComplete(sender, exchange);
            return;
        }
        exchange.getResponseHeaders().add("Content-Length", String.valueOf(contentLength));
        serveImpl(sender, exchange, 0, contentLength, callback, false);
    }

    public void serveRange(Sender sender, HttpServerExchange exchange, long start, long end, IoCallback callback) throws IOException {
        long contentLength = getContentLength();
        if (contentLength == null || start >= contentLength || end > contentLength || start > end) {
            exchange.getResponseHeaders().add("Content-Range", "bytes */" + contentLength);
            exchange.getResponseHeaders().add("Content-Length", "0");
            callback.onWriteComplete(sender, exchange);
            return;
        }
        exchange.getResponseHeaders().add("Content-Range", "bytes " + start + "-" + end + "/" + contentLength);
        serveImpl(sender, exchange, start, end, callback, true);
