            MessageProducer topicProducer = session.createProducer(topic);

            sendMessage(session, queueProducer, "QUEUE");
            sendMessage(session, topicProducer, "TOPIC");

            queueProducer.close();
            topicProducer.close();
            session.close();
            connection.close();
        } catch (NamingException | JMSException e) {
            e.printStackTrace();
        }
    }

    private void sendMessage(Session session, MessageProducer messageProducer, String type) throws JMSException {
        for (int i = 0; i < SEND_NUM; i++) {
            TextMessage message = session.createTextMessage("Message " + i + " sent to " + type + " at " + new Date());
            messageProducer.send(message);
            System.out.println("Sent message to " + type + " with timestamp: " + new Date());
