  public ClusteredEventBus(VertxInternal vertx, VertxOptions options) {
    super(vertx);
    this.options = options.getEventBusOptions();
  }

  private NetServerOptions getServerOptions() {
    return new NetServerOptions(this.options.toJson());
  }

