        connection.setRequestMethod("POST");
        connection.setConnectTimeout(CONNECT_TIMEOUT);
        connection.setReadTimeout(READ_TIMEOUT);
        connection.setDoOutput(true);

        DataOutputStream wr = new DataOutputStream(connection.getOutputStream());
        wr.writeBytes(param.toString());
        wr.flush();
        wr.close();

        return handleResponse(connection);
    }

    public static String handleResponse(HttpURLConnection httpURLConnection) throws IOException {
        if (httpURLConnection.getResponseCode() == HttpURLConnection.HTTP_OK) {
            return readResult(httpURLConnection);
        } else {
            return handleError(httpURLConnection);
        }
    }

    public static String readResult(HttpURLConnection urlConnection) throws IOException {
        String encoding = urlConnection.getContentEncoding();
        if (encoding != null && "gzip".equals(encoding)) {
            return new BufferedReader(new InputStreamReader(new GZIPInputStream(urlConnection.getInputStream()))).lines().reduce("", (a, b) -> a + b);
        } else {
            return new BufferedReader(new InputStreamReader(urlConnection.getInputStream())).lines().reduce("", (a, b) -> a + b);
        }
    }

    public static String handleError(HttpURLConnection urlConnection) throws IOException {
        StringBuilder response = new StringBuilder();
        try (BufferedReader in = new BufferedReader(new InputStreamReader(urlConnection.getErrorStream()))) {
            String inputLine;
            while ((inputLine = in.readLine()) != null) {
                response.append(inputLine);
            }
        }
        throw new IOException(response.toString());
    }
}
