        StringBuilder line = new StringBuilder();
        while (true) {
            int c = is.read();
            switch (c) {
                case -1:
                    throw new RuntimeException("Unexpected EOF");
                case '\r':
                    continue;
                case '\n':
                    return line.toString();
                default:
                    line.append((char) c);
            }
        }
    }

    private void readResponse(SSLSocket sslSocket) throws IOException {
