import java.io.*;
import java.net.HttpURLConnection;
import java.net.URL;
import java.nio.charset.StandardCharsets;
import java.util.Base64;
import java.util.Iterator;
import java.util.NoSuchElementException;
import java.util.Objects;
import java.util.concurrent.CompletableFuture;
import java.util.function.Consumer;
import java.util.stream.Stream;

final class RestClient {

    private static final int BUFFER_SIZE = 1024;
    private static final Iterator<String> NO_ITERATOR = new Iterator<String>() {
        @Override
        public boolean hasNext() { return false; }

        @Override
        public String next() {
            throw new NoSuchElementException("This method should never be called.");
        }
    };

    private final Protocol protocol;
    private final String host;
    private final int port;
    private final String username;
    private final String password;

    RestClient(Protocol protocol, String host, int port, String username, String password) {
        this.protocol = Objects.requireNonNull(protocol);
        this.host     = Objects.requireNonNull(host);
        this.port     = port;
        this.username = username;
        this.password = password;
    }

    protected URL getUrl(String path, Param... param) {
        final StringBuilder url = new StringBuilder()
            .append(getProtocol())
            .append("://")
            .append(host);

        if (port > 0) {
            url.append(":").append(port);
        }

        url.append("/").append(path);

        if (param.length > 0) {
            url.append(
                Stream.of(param)
                    .map(p -> encode(p.getKey()) + "=" + encode(p.getValue()))
                    .collect(joining("&", "?", ""))
            );
        }

        try {
            return new URL(url.toString());
        } catch (Exception ex) {
            throw new IllegalArgumentException("Error building URL", ex);
        }
    }

    private CompletableFuture<Response> send(Method method, String path, Option[] options, StreamConsumer outStreamConsumer) {
        return CompletableFuture.supplyAsync(() -> {
            HttpURLConnection conn = null;
            try {
                final Param[] params = Stream.of(options)
                    .filter(o -> o.getType() == PARAM)
                    .map(Param.class::cast)
                    .toArray(Param[]::new);

                final Header[] headers = Stream.of(options)
                    .filter(o -> o.getType() == HEADER)
                    .map(Header.class::cast)
                    .toArray(Header[]::new);

                conn = (HttpURLConnection) getUrl(path, params).openConnection();
                conn.setRequestMethod(method.name());
                conn.setUseCaches(false);
                conn.setAllowUserInteraction(false);

                if (username != null && password != null) {
                    final String encoding = Base64.getEncoder()
                        .encodeToString((username + ":" + password).getBytes());
                    conn.setRequestProperty("Authorization", "Basic " + encoding);
                }

                for (final Header header : headers) {
                    conn.setRequestProperty(header.getKey(), header.getValue());
                }

                final boolean doOutput = outStreamConsumer != IGNORE;
                conn.setDoOutput(doOutput);
                conn.connect();

                if (doOutput) {
                    try (OutputStream out = conn.getOutputStream()) {
                        outStreamConsumer.writeTo(out);
                        out.flush();
                    }
                }

                int status = getResponseCodeFrom(conn);
                String text = tryGetResponseTextFrom(conn, status);
                return new Response(status, text, conn.getHeaderFields());
            } catch (Exception ex) {
                throw new RestException(ex, protocol, method, username, host, port, path, options);
            } finally {
                if (conn != null) conn.disconnect();
            }
        });
    }

    private static String tryGetResponseTextFrom(HttpURLConnection conn, int status) throws IOException {
        try (BufferedReader rd = new BufferedReader(
            new InputStreamReader(status >= 400 
                ? conn.getErrorStream() 
                : conn.getInputStream()))) {

            StringBuilder sb = new StringBuilder();
            String line;
            while ((line = rd.readLine()) != null) {
                sb.append(line);
            }
            return sb.toString();
        }
    }

    private static int getResponseCodeFrom(HttpURLConnection conn) throws IOException {
        try {
            return conn.getResponseCode();
        } catch (FileNotFoundException ex) {
            return 404;
        }
    }

    @FunctionalInterface
    private interface StreamConsumer {
        void writeTo(OutputStream out) throws IOException;
    }

    private static final class SingletonIterator<E> implements Iterator<E> {
        private final E e;
        private boolean hasNext = true;

        private SingletonIterator(E e) { this.e = e; }

        public boolean hasNext() { return hasNext; }

        public E next() {
            if (hasNext) {
                hasNext = false;
                return e;
            }
            throw new NoSuchElementException();
        }
    }
}

