        connection.setDoOutput(true);
        connection.setConnectTimeout(connectTimeout);
        connection.setReadTimeout(readTimeout);
        connection.setRequestProperty("Content-Type", "multipart/form-data; boundary=" + getBoundary());

        try (DataOutputStream outputStream = new DataOutputStream(connection.getOutputStream())) {
            for (Map.Entry<String, String> entry : textMap.entrySet()) {
                outputStream.writeBytes("--" + getBoundary() + "\r\n");
                outputStream.writeBytes("Content-Disposition: form-data; name=\"" + entry.getKey() + "\"\r\n");
                outputStream.writeBytes("Content-Type: text/plain; charset=UTF-8\r\n");
                outputStream.writeBytes("\r\n");
                outputStream.writeBytes(entry.getValue());
                outputStream.writeBytes("\r\n");
            }
            for (Map.Entry<String, MultipartFile> entry : fileMap.entrySet()) {
                outputStream.writeBytes("--" + getBoundary() + "\r\n");
                outputStream.writeBytes("Content-Disposition: form-data; name=\"" + entry.getKey() + "\"; filename=\"" + entry.getValue().getFileName() + "\"\r\n");
                outputStream.writeBytes("Content-Type: " + entry.getValue().getContentType() + "\r\n");
                outputStream.writeBytes("\r\n");
                outputStream.write(entry.getValue().getFileData());
                outputStream.writeBytes("\r\n");
            }
            outputStream.writeBytes("--" + getBoundary() + "--\r\n");
            outputStream.flush();
        }

        StringBuilder response = new StringBuilder();
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(connection.getInputStream(), StandardCharsets.UTF_8))) {
            String line;
            while ((line = reader.readLine()) != null) {
                response.append(line).append("\r\n");
            }
        }

        return response.toString();
    }

    private static String getBoundary() {
        return Long.toHexString(System.currentTimeMillis());
    }

    public static void closeStream(Closeable closeable) {
        if (closeable != null) {
            try {
                closeable.close();
            } catch (IOException e) {
                logger.severe("Error closing stream: " + e.getMessage());
            }
        }
    }
}
